    <div>
        <h1>{props.heading} - {text}</h1>
        <div className="mb-3">
            <textarea className="form-control" id="myBox" rows="8"></textarea>
        </div>
        <button className="btn btn-primary">Convert to Uppar case</button>
    </div>
  )
}
