        <Routes>
          <Route exact path="about" element={<About />}>
          </Route>
          <Route exact path="/" element={<TextForm showAlert={showAlert} heading="Enter the text below to analyze" mode={mode}/>}>  
          </Route>
        </Routes>
      </div>
      </Router> */}
    </>
  );
}

export default App;
